import "./intro.scss"


export default function intro({menuOpen,setMenuOpen}) {
  var toggleSwitch = document.querySelector(".toggle-button");
  function switchTheme(e) {
    if (e.target.checked) {
      document.documentElement.setAttribute("data-theme", "dark");
      /*If required*/
      document.body.classList.add("dark-mode");
      document.body.classList.remove("light-mode");
    } else {
      document.documentElement.setAttribute("data-theme", "light");
      /*If required*/
      document.body.classList.add("light-mode");
      document.body.classList.remove("dark-mode");
    }
  }
  
  toggleSwitch.addEventListener("change", switchTheme, false);
  return (
    <div className="intro" id="intro" >
      
        <div className="left">
        <div className="imgContainer btext">
        &lt;/&gt;
        </div>
          
        </div>
        <div className="right">
        <input type="checkbox" class="toggle-button"></input>
        <div className="btext">
        
        Hi, I'm aymen
        <br></br>a web developer.
        </div>
        </div>
    </div>
  )
}
